import React from 'react'
import fetch from 'isomorphic-unfetch'
import Layout from '../components/layout'
import Link from "next/link"
import 'bootstrap/dist/css/bootstrap.min.css';
import { Card, Button } from 'react-bootstrap'

const Index = props => (
    <Layout>
        <h1>Headline</h1>
        <ul>
            {props.newsUs.map(news => (
                <li>
                    <a href={news.url} target="_blank">{news.title}</a>
                </li>
            ))}
        </ul>
    </Layout>
);

Index.getInitialProps = async function () {
    const res = await fetch('http://newsapi.org/v2/top-headlines?country=us&category=technologies&apiKey=72aa24ac8bbf4a1aa302891fbde1d0ac');
    const data = await res.json();
    const newsUs = data.articles.slice(0, 8)
    console.log(`Show data fetched. Count: ${newsUs.length}`);

    return {
        newsUs
    };
};

export default Index;
