import React from 'react'
import fetch from 'isomorphic-unfetch'
import Layout from '../components/layout'
import NewsSection from '../components/NewsSection'

import { Container, Row } from 'react-bootstrap'
const Index = props => (
    <Layout>
        <Container>
            <NewsSection headline={props.headline} articles={props.newsUs} />
        </Container>
    </Layout>
);

Index.getInitialProps = async function () {
    const res = await fetch('http://newsapi.org/v2/top-headlines?country=id&category=business&apiKey=72aa24ac8bbf4a1aa302891fbde1d0ac');
    const data = await res.json();
    const newsUs = data.articles.slice(1, 8)
    console.log(`Show data fetched. Count: ${newsUs.length}`);

    return {
        headlineUs: data.articles[0],
        newsUs
    };
};

export default Index;
